import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import React from "react";
import { useState } from 'react';
import { useForm } from "react-hook-form";
import axios from "axios";


const App = (props) => { 

  const {reset} = useForm();
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);



  const [values, setValues]  = useState({
    city: '',
  })



  const handleChange = (event) => {
    setValues({...values, [event.target.name]: event.target.value});

  }


  const handleSubmit = (event) => {



            var city = values.city


            //alert(obj)
            axios.get("http://localhost:8080/v1/current-weather/"+city)
            .then(response => {
              setData(response.data);
              setLoading(false); // Set loading to false on successful fetch
            })
            .catch(error => {
              setError(error);
              setLoading(false); // Set loading to false on error
            });


    reset();
    event.preventDefault();
  }

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }

  return (
  <div className="page">
  <div className="form-container">
    <div className="form-box">
      <div className="header-form">
        <h4 className="text-primary text-center">

        </h4>
        <div className="image"></div>
      </div>

      <br/><br/><br/>
      <div className="body-form">
        <form className="m-3" onSubmit={handleSubmit}>

          <div className="input-group mb-2">
            <div className="input-group-prepend">
              <h2>WEATHER PORTFOLIO</h2>
              <br></br><br></br>
            </div>
            <input
              type="text"
              className="form-control"
              placeholder="City Name"
              onChange={handleChange}
              required

              name="city"
              value={values.city}
            />
          </div>
          <br/><br/>
          <button type="submit" className="btn btn-secondary btn-block">
            Submit
          </button>
        </form>
      </div>
      <p>Temperature: {data.mainData.temperature} Â°C</p>
      <p>Humidity: {data.mainData.humidity}%</p> 
    </div>
  </div>
  </div>
  );

}



export default App;
