import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import React from "react";
import { useState } from 'react';
import { useForm } from "react-hook-form";
import axios from "axios";


const App = (props) => {

  const { reset } = useForm();
  const [data, setData] = useState([]);


  const [values, setValues] = useState({
    city: '',
  })



  const handleChange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value });

  }


  const handleSubmit = (event) => {



    var city = values.city


    //alert(obj)
    axios.get("http://localhost:8080/v1/current-weather/" + city)
      .then(response => {
        console.log(response.data.mainData)
        setData(response.data.mainData);
      })
      .catch(error => {
        console.log(error)


        alert(error)

      });


    reset();
    event.preventDefault();
  }


  return (
    // <div className="page">
    //   <div className="form-container">
    //     <div className="form-box">
    //       <div className="header-form">
    //         <h4 className="text-primary text-center">

    //         </h4>
    //         <div className="image"></div>
    //       </div>

    //       <br /><br /><br />
    //       <div className="body-form">
    //         <form className="m-3" onSubmit={handleSubmit}>

    //           <div className="input-group mb-2">
    //             <div className="input-group-prepend">
    //               <h2>WEATHER PORTFOLIO</h2>
    //               <br></br><br></br>
    //             </div>
    //             <input
    //               type="text"
    //               className="form-control"
    //               placeholder="City Name"
    //               onChange={handleChange}
    //               required

    //               name="city"
    //               value={values.city}
    //             />
    //           </div>
    //           <br /><br />
    //           <button type="submit" className="btn btn-secondary btn-block">
    //             Submit
    //           </button>
    //         </form>
    //       </div>



    //       Current Temperature : {data.temperature} <br></br>
    //       Current Humidity : {data.humidity}
    //     </div>
    //   </div>
    // </div>

    <div class="container">
        <div class="search-box">
            <input type="text" placeholder="Enter your location"></input>
            <button type="submit"></button>
        </div>


               <form onSubmit={handleSubmit}>

               <div className="container">
               <div class="search-box">
                 <input
                  type="text"

                  placeholder="City Name"
                  onChange={handleChange}
                  required

                  name="city"
                  value={values.city}
                />
                </div>
              </div>
              <br /><br />
              <button type="submit">
                Submit
              </button>
            </form>

            Current Temperature : {data.temperature} <br></br>
           Current Humidity : {data.humidity}

        <div class="weather-box">

            <p class="temperature"></p>

        </div>

        <div class="weather-details">
            <div class="humidity">
                <i class="fa-solid fa-water"></i>
                <div class="text">
                    <span></span>
                    <p>Humidity</p>
                </div>
            </div>

            <div class="wind">
                <i class="fa-solid fa-wind"></i>
                <div class="text">
                    <span></span>
                    <p>Wind Speed</p>
                </div>
            </div>
        </div>

    </div>
  );

}



export default App;
