import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import React from "react";
import { useState } from 'react';
import { useForm } from "react-hook-form";
import axios from "axios";


const App = (props) => { 

  const {reset} = useForm();

  const [values, setValues]  = useState({
    lasttransactionamount: '',
    mobileNo:'',
  })



  const handleChange = (event) => {
    setValues({...values, [event.target.name]: event.target.value});

  }


  const handleSubmit = (event) => {

    if(true)
    {
        if (localStorage.getItem('token')!=null)
        {
            var obj = {
                "mobileNo":localStorage.getItem('mobileNo'),
                "lasttransactionamount":values.lasttransactionamount
            }
            //alert(obj)
            axios.post("http://localhost:8080/api/transaction/deposit",obj)
                        .then(response=>{  
                            console.log(response)
                            alert(response.data)
                            props.history.push({pathname: '/welcome',state: {
                              data: "Successful" ,
                              description: response.data.description,
                              id : response.data.data
                            } })
                        })
                        .catch(error => {
                            console.log(error)

                            if(error.response.status == 400)
                            {
                              alert("You cannot recharge wallet with amount less than equal to zero")
                            }
                            else{
                              alert(error)
                            }
                        });
        }
    }    
    reset();
    event.preventDefault();
  }


  return (
  <div className="page">
  <div className="form-container">
    <div className="form-box">
      <div className="header-form">
        <h4 className="text-primary text-center">

        </h4>
        <div className="image"></div>
      </div>

      <br/><br/><br/>
      <div className="body-form">
        <form className="m-3" onSubmit={handleSubmit}>

          <div className="input-group mb-3">
            <div className="input-group-prepend">
              <h2>WEATHER PORTFOLIO</h2>
              <br></br><br></br>
            </div>
            <input
              type="text"
              className="form-control"
              placeholder="City Name"
              onChange={handleChange}
              required

              name="city"
              value={values.city}
            />
          </div>
          <br/><br/>
          <button type="submit" className="btn btn-secondary btn-block">
            Submit
          </button>
        </form>
      </div>
    </div>
  </div>
  </div>
  );

}



export default App;
